    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // Insert
        try {
            Class.forName("java.sql.DriverManager");
            Connection con=DriverManager.getConnection("jdbc:mysql://localhost:3306/project","root","9829");
            String name,emailID,password,sql;
            name=t1.getText();
            emailID=t2.getText();
            password=new String(jPasswordField1.getPassword());
            
            if((name.equals(""))||(emailID.equals(""))||(password.equals("")))
          {
            JOptionPane.showMessageDialog(this,"Fill all details");
          }
              else
            {
              Statement st=con.createStatement();
              sql="insert into users values('"+name+"','"+emailID+"','"+password+"');";
              st.executeUpdate(sql);
            
               JOptionPane.showMessageDialog(this,"Account Created");
               
               t1.setText("");
               t2.setText("");
               jPasswordField1.setText("");
               jPasswordField2.setText("");
               
               JOptionPane.showMessageDialog(this,"Click on Login");
            }
        }
        
        catch (Exception e) {
            JOptionPane.showMessageDialog(this,e.getMessage());
        }
        
       
        
}//GEN-LAST:event_jButton1ActionPerformed

