    private void jPasswordField2CaretUpdate(javax.swing.event.CaretEvent evt) {//GEN-FIRST:event_jPasswordField2CaretUpdate
        // TODO add your handling code here:
        String pwd1=new String(jPasswordField1.getPassword());
        String pwd2=new String(jPasswordField2.getPassword());
        if(pwd1.equals(pwd2))
        {
            l1.setText("Password verified");
        }
        else
        {
            l1.setText("Password do not MATCH");
        }
    }//GEN-LAST:event_jPasswordField2CaretUpdate

